import React, { useEffect } from "react";
import { motion } from 'framer-motion';
import Image from "./Image";

const container = {
  show: {
    transition: {
      staggerChildren: 0.35,
    }
  }
};

const item = {
  hidden: {
    opacity: 0,
    y: 200,
  },
  show: {
    opacity: 1,
    y: 0,
    transition: {
      ease: [.6, .01,-.05, .95],
      duration: 1.6 
    }
  },
  exit: {
    opacity: 0,
    y: -200,
    transition: {
      ease: "easeInOut",
      duration: 0.8
    }
  }
}

const Loader = ({ setLoading }) => {
  useEffect(() => {
    const timer = setTimeout(() => {
      setLoading(false);
    }, 4000);
    return () => clearTimeout(timer);
  });

  return (
    <div className='loader'>
      <motion.div
        className='loader-inner'
        variants={container}
        initial="hidden"
        animate="show"
        exit="exit"
      >
        <motion.ImageBlock
          id='image-1'
          variants={item}
        />
        <div className='transition-image'>
          <img
            src={process.env.PUBLIC_URL + `/images/image-2.jpg`}
            alt='random alt'
          />
        </div>
        <motion.ImageBlock
          id='image-3'
          variants={item}
        />
        <motion.ImageBlock
          id='image-4'
          variants={item}
        />
        <motion.ImageBlock
          id='image-5'
          variants={item}
        />
      </motion.div>
    </div>
  );
};

export const ImageBlock = ({ id }) => {
  return (
    <motion.div
      className={`image-block ${id}`}
      animate={{
        scale: 0.5,
        transition: { duration: 1 }
      }}
    >
      <Image
        src={process.env.PUBLIC_URL + `/images/${id}.webp`}
        fallback={process.env.PUBLIC_URL + `/images/${id}.jpg`}
        alt={id}
      />
    </motion.div>
  );
};
export default Loader;
